<?php		if ( ! function_exists( 'TheChameleon_get_body_class' ) ) :		/**		 * Make new body clasees 		 *		 * Koritim ovo da bi promenuo imena kalsa za body 		 *		 * @author Goran Petrovic		 * @since 1.0		 *		 * @param string $class 		 * @param boolean $echo 		 * @return string  classes names		 **/					function TheChameleon_get_body_class( $class = 'custom', $echo = false ){							ob_start();					body_class();						$body_class = ob_get_contents();			ob_end_clean();			$body_class = str_replace(array('class="', '"'),array('',''),	$body_class);			if(!empty($class)) :							$class_array = explode(" ", $body_class);									//lats class fix				$last = count($class_array)-1;					$class_array[$last] = $class_array[$last].'-'.$class;				$body_class = implode("_$class ", $class_array);							endif;			if($echo) :				echo $body_class;						else:				return $body_class;				endif;		}	endif;		if ( ! function_exists( 'TheChameleon_get_current_url' ) ) :		/**	 * TheChameleon_get_current_url	 *	 * @author Goran Petrovic	 * @since 1.0	 *	 **/	function TheChameleon_get_current_uri( $queries = TRUE ) {				$current_url = 	( is_ssl() || force_ssl_admin() || force_ssl_login() ) ? 'https://'.$_SERVER["HTTP_HOST"] . $_SERVER["REQUEST_URI"] : 'http://'.$_SERVER["HTTP_HOST"] . $_SERVER["REQUEST_URI"] ; 				if($queries) :			$the_url = 	$current_url;		else:						$parts = parse_url("$current_url");					$the_url =	( is_ssl() || force_ssl_admin() || force_ssl_login() ) ? 'https://'.$parts["host"] . $parts["path"] : 'http://'.$parts["host"] . $parts["path"] ; 					endif;		return $the_url;		}	endif;				?>